const xmlData = $input.first().json.data;

// Function to extract text from a tag, handling CDATA sections
function getTextContent(element, tagName) {
  const regex = new RegExp(`<${tagName}>(.*?)<\/${tagName}>`, 's');
  const match = element.match(regex);
  if (match && match[1]) {
    // Check for CDATA section and extract content
    const cdataMatch = match[1].match(/<!\[CDATA\[(.*?)\]\]>/s);
    return cdataMatch ? cdataMatch[1].trim() : match[1].trim();
  }
  return '';
}

// Function to parse the XML string and extract relevant data
function parseRssFeed(xml) {
  const items = xml.match(/<item>.*?<\/item>/gs);
  if (!items) {
    return [];
  }

  const output = [];
  for (const item of items) {
    const title = getTextContent(item, 'title');
    const link = getTextContent(item, 'link');
    const pubDate = getTextContent(item, 'pubDate');
    // Extract the description from the content:encoded tag
    const description = getTextContent(item, 'content:encoded');

    let timeAgo = '';
    if (pubDate) {
      // Calculate how long ago the article was published
      const now = new Date();
      const pubDateObj = new Date(pubDate);
      const diffInMilliseconds = now - pubDateObj;
      const diffInMinutes = Math.floor(diffInMilliseconds / (1000 * 60));
      const diffInHours = Math.floor(diffInMilliseconds / (1000 * 60 * 60));

      if (diffInMinutes < 60) {
        timeAgo = `${diffInMinutes} minutes ago`;
      } else {
        timeAgo = `${diffInHours} hours ago`;
      }
    }

    // Only include items that have all the required fields
    // The check for `timeAgo` also ensures a valid pubDate was found
    if (title && link && pubDate && description && timeAgo) {
      output.push({
        title: title,
        link: link,
        timeAgo: timeAgo,
        description: description
      });
    }
  }
  return output;
}

// Parse the data and prepare the output for n8n
const articles = parseRssFeed(xmlData);

// Set the output for the n8n node
return articles;

