}

// Function to extract the first <p> snippet from raw HTML content
function getSnippetFromHtml(html) {
  const match = html.match(/<p>(.*?)<\/p>/s);
  return match ? match[1].trim() : '';
}

// Function to calculate how long ago the pubDate was
function getTimeAgo(pubDateStr) {
  const pubDate = new Date(pubDateStr);
  const now = new Date();
  const diffMs = now - pubDate;
  const diffMins = Math.floor(diffMs / 60000);

  if (diffMins < 1) return 'Just now';
  if (diffMins < 60) return `${diffMins} minute${diffMins !== 1 ? 's' : ''} ago`;

  const diffHours = Math.floor(diffMins / 60);
  if (diffHours < 24) return `${diffHours} hour${diffHours !== 1 ? 's' : ''} ago`;

  const diffDays = Math.floor(diffHours / 24);
  return `${diffDays} day${diffDays !== 1 ? 's' : ''} ago`;
}

// Function to parse the XML string and extract relevant data
function parseRssFeed(xml) {
  const items = xml.match(/<item>.*?<\/item>/gs);
  if (!items) return [];

  const output = [];

  for (const item of items) {
    const title = getTextContent(item, 'title');
    const link = getTextContent(item, 'link');
    const pubDate = getTextContent(item, 'pubDate');
    const description = getTextContent(item, 'description') || getTextContent(item, 'content:encoded');
    const snippet = getSnippetFromHtml(description);
    const timeAgo = pubDate ? getTimeAgo(pubDate) : '';

    if (title && link && pubDate) {
      output.push({
        title,
        link,
        timeAgo,
        snippet
      });
    }
  }

  return output;
}

// Parse and return for n8n
const articles = parseRssFeed(xmlData);
return articles;

