// Match each article block
const articleBlocks = htmlData.match(/<div class="uikit-row">[\s\S]*?<\/div><\/div><\/div>/g);

if (!articleBlocks) {
  return [];
}

const results = [];

for (const block of articleBlocks) {
  // Extract link
  const linkMatch = block.match(/<a[^>]*href="([^"]+)"[^>]*class="[^"]*cmc-link[^"]*">/);
  const link = linkMatch ? linkMatch[1] : null;

  // Extract title
  const titleMatch = block.match(/class="[^"]*cmc-link[^"]*">([^<]+)<\/a>/);
  const title = titleMatch ? titleMatch[1].trim() : null;

  // Extract posted time like "03:23"
  const timeMatch = block.match(/<p[^>]*class="[^"]*fdCzQf[^"]*"[^>]*>([^<]+)<\/p>/);
  const postedTime = timeMatch ? timeMatch[1].trim() : null;

  // Convert postedTime to "x minutes/hours ago"
  let timeAgo = null;
  if (postedTime) {
    const now = new Date();
    const [hours, minutes] = postedTime.split(':').map(Number);

    const postDate = new Date();
    postDate.setHours(hours);
    postDate.setMinutes(minutes);
    postDate.setSeconds(0);
    postDate.setMilliseconds(0);

    // If the post time is ahead of current time, assume it was posted yesterday
    if (postDate > now) {
      postDate.setDate(postDate.getDate() - 1);
    }

    const diffMs = now - postDate;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMins / 60);

    if (diffMins < 1) {
      timeAgo = "Just now";
    } else if (diffMins < 60) {
      timeAgo = `${diffMins} minute${diffMins > 1 ? 's' : ''} ago`;
    } else {
      timeAgo = `${diffHours} hour${diffHours > 1 ? 's' : ''} ago`;
    }
  }

  // Extract publisher (e.g., COINTURK NEWS)
  const publisherMatch = block.match(/<span[^>]*class="[^"]*ehyBa-d[^"]*"[^>]*>([^<]+)<\/span>/);
  const publisher = publisherMatch ? publisherMatch[1].trim() : null;

  // Extract tags (multiple spans with class eydMEP)
  const tagMatches = [...block.matchAll(/<span[^>]*class="[^"]*eydMEP[^"]*"[^>]*>([^<]+)<\/span>/g)];
  const tags = tagMatches.map(match => match[1].trim());

  if (title && link && timeAgo) {
    results.push({
      json: {
        title,
        link,
        timeAgo,
        publisher,
        tags,
      },
    });
  }
}

return results;

