// Access the input data from the n8n variable
const inputData = $input.first().json.data;

// First, un-escape the string.
// We replace the Unicode escape sequences with their actual characters.
let unescapedHtml = inputData
  .replace(/\\u003c/g, '<') // Replace escaped '<' with '<'
  .replace(/\\u003e/g, '>') // Replace escaped '>' with '>'
  .replace(/\\n/g, '\n')     // Replace escaped newlines
  .replace(/\\\\/g, '\\');   // Replace double backslashes with a single one

// Array to store the clean paragraph text
const paragraphs = [];

// A regular expression to find all content within a <p> tag.
// The 'gi' flags ensure it's a global, case-insensitive search.
const regex = /<p[^>]*>([\s\S]*?)<\/p>/gi;
let match;

// Loop through the HTML to find every paragraph.
while ((match = regex.exec(unescapedHtml)) !== null) {
  // `match[1]` contains the inner text of the paragraph tag.
  let content = match[1];

  // Clean up any remaining HTML tags (like <strong> or <a>) inside the paragraph.
  let cleanedText = content.replace(/<[^>]+>/g, '').trim();

  // Make sure we're not adding empty paragraphs.
  if (cleanedText) {
    paragraphs.push(cleanedText);
  }
}

// Join the collected paragraphs with a double newline for clear spacing.
const finalParagraphs = paragraphs.join('\n\n');

// Prepare the output for the n8n workflow.
const output = [{
  json: {
    paragraphContent: finalParagraphs
  }
}];

return output;

// Load cheerio if available in n8n, otherwise use RegExp workaround.
// This code assumes the input HTML is in the `data` field of the first item.
const html = $input.first().json.data;

// Regex fallback (cheerio not available in n8n by default)
function extractParagraphsFromPostContent(html) {
  const divMatch = html.match(/<div[^>]*class=["']post-content relative["'][^>]*>([\s\S]*?)<\/div>/);
  if (!divMatch || divMatch.length < 2) return [];

  const divContent = divMatch[1];
  const paragraphRegex = /<p[^>]*>(.*?)<\/p>/g;
  const paragraphs = [];
  let match;
  while ((match = paragraphRegex.exec(divContent)) !== null) {
    paragraphs.push(match[1].trim());
  }
  return paragraphs;
}

// Extract
const paragraphs = extractParagraphsFromPostContent(html);

// Return as individual items
return paragraphs.map(p => ({ json: { paragraph: p } }));
